import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShoppingCart, Search, Heart } from 'lucide-react';

import '../components/latest.css';
// Assuming you have these images imported or use appropriate URLs


export default function ProductSales() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('new');
  const [products, setProducts] = useState([
    {
      id: 1,
      name: 'Comfort Handy Craft',
      price: 42.00,
      oldPrice: 65.00,
      image: 'https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcQvcWgr-S5BTNre1zHazRbMUD6DAvxGHxHh7hxsltFYu1dhwndi',
      isSale: false,
      category: 'new',
      isFavorite: false
    },
    {
      id: 2,
      name: 'Comfort Handy Craft',
      price: 42.00,
      oldPrice: 65.00,
      image: 'data:image/jpeg;base64,',
      isSale: true,
      category: 'new',
      isFavorite: false
    },
    {
      id: 3,
      name: 'Comfort Handy Craft',
      price: 42.00,
      oldPrice: 65.00,
      image: 'data:image/jpeg;base64,',
      isSale: false,
      category: 'new',
      isFavorite: false
    },
    {
        id: 4,
        name: 'Comfort Handy Craft',
        price: 42.00,
        oldPrice: 65.00,
        image: 'data:image/jpeg;base64,',
        isSale: false,
        category: 'new',
        isFavorite: false
      },
      {
        id: 5,
        name: 'Comfort Handy Craft',
        price: 42.00,
        oldPrice: 65.00,
        image: 'data:image/jpeg;base64,',
        isSale: false,
        category: 'new',
        isFavorite: false
      },
      {
        id:6,
        name: 'Comfort Handy Craft',
        price: 42.00,
        oldPrice: 65.00,
        image: 'data:image/jpeg;base64,',
        isSale: false,
        category: 'new',
        isFavorite: false
      },
    {
      id: 7,
      name: 'Comfort Handy Craft',
      price: 42.00,
      oldPrice: 65.00,
      image: 'data:image/jpeg;base64,',
      isSale: false,
      category: 'best',
      isFavorite: false
    },
    {
      id: 8,
      name: 'Comfort Handy Craft',
      price: 42.00,
      oldPrice: 65.00,
      image: 'data:image/jpeg;base64,',
      isSale: false,
      category: 'featured',
      isFavorite: false
    },
    {
      id: 9,
      name: 'Comfort Handy Craft',
      price: 42.00,
      oldPrice: 65.00,
      image: 'data:image/jpeg;base64,',
      isSale: true,
      category: 'special',
      isFavorite: false
    }
  ]);

  const [cartItems, setCartItems] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(savedCart);

    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || {};
    setProducts(prevProducts => 
      prevProducts.map(product => ({
        ...product,
        isFavorite: savedFavorites[product.id] || false
      }))
    );
  }, []);

  useEffect(() => {
    filterProducts(activeTab);
  }, [activeTab, products]);

  const filterProducts = (category) => {
    let filtered = products.filter(product => product.category === category);
    if (category !== 'new') {
      filtered = filtered.slice(0, 2); // Show only up to 2 products for non-new categories
    }
    setFilteredProducts(filtered);
  };

  const addToCart = (product) => {
    const updatedCart = [...cartItems, product];
    setCartItems(updatedCart);
    localStorage.setItem('cartItems', JSON.stringify(updatedCart));
    localStorage.setItem('cartCount', updatedCart.length);
    navigate('/cart');
  };

  const toggleFavorite = (productId) => {
    setProducts(prevProducts => 
      prevProducts.map(product => 
        product.id === productId 
          ? { ...product, isFavorite: !product.isFavorite }
          : product
      )
    );

    const updatedFavorites = products.reduce((acc, product) => {
      if (product.id === productId) {
        acc[product.id] = !product.isFavorite;
      } else if (product.isFavorite) {
        acc[product.id] = true;
      }
      return acc;
    }, {});

    localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
  };

  const quickView = (product) => {
    // Implement quick view functionality here
    console.log('Quick view for:', product.name);
  };

  return (
    <div className="ps-container">
      <h2 className="ps-title">Leatest Products</h2>
      
      <div className="ps-tabs">
        <button 
          className={`ps-tabs__btn ${activeTab === 'new' ? 'ps-tabs__btn--active' : ''}`}
          onClick={() => setActiveTab('new')}
        >
          New Arrival
        </button>
        <button 
          className={`ps-tabs__btn ${activeTab === 'best' ? 'ps-tabs__btn--active' : ''}`}
          onClick={() => setActiveTab('best')}
        >
          Best Seller
        </button>
        <button 
          className={`ps-tabs__btn ${activeTab === 'featured' ? 'ps-tabs__btn--active' : ''}`}
          onClick={() => setActiveTab('featured')}
        >
          Featured
        </button>
        <button 
          className={`ps-tabs__btn ${activeTab === 'special' ? 'ps-tabs__btn--active' : ''}`}
          onClick={() => setActiveTab('special')}
        >
          Special Offer
        </button>
      </div>

      <div className="ps-grid">
        {filteredProducts.map((product) => (
          <div key={product.id} className="ps-card">
            {product.isSale && <div className="ps-card__badge"><a href="/products" className="ps-card__badge-link">Sale</a></div>}
            <div className="ps-card__actions">
              <button
                className="ps-card__action-btn"
                onClick={() => addToCart(product)}
                aria-label="Add to cart"
              >
                <ShoppingCart size={16} />
              </button>
              <button 
                className="ps-card__action-btn"
                onClick={() => quickView(product)}
                aria-label="Quick view"
              >
                <Search size={16} />
              </button>
              <button
                className={`ps-card__action-btn ${product.isFavorite ? 'ps-card__action-btn--favorite' : ''}`}
                onClick={() => toggleFavorite(product.id)}
                aria-label="Add to favorites"
              >
                <Heart 
                  size={16} 
                  fill={product.isFavorite ? 'currentColor' : 'none'} 
                />
              </button>
            </div>
            <div className="ps-card__image">
              <img 
                src={product.image} 
                alt={product.name} 
                className="ps-card__img"
              />
            </div>
            <div className="ps-card__info">
              <h3 className="ps-card__name">{product.name}</h3>
              <div className="ps-card__price">
                <span className="ps-card__price-current">${product.price.toFixed(2)}</span>
                <span className="ps-card__price-old">${product.oldPrice.toFixed(2)}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
